#pragma once
#include "TextureResource.h"
#include "Loadable.h"
#include <memory>

class Texture : public Loadable
{
	// Texture Resource Config
	std::shared_ptr<TextureResource> tr;

	// TextureId generated by glGenTextures()
	GLuint textureId;

public:
	// Constructor
	Texture(std::shared_ptr<TextureResource> tr = nullptr);

	// Initializer
	Texture& init(std::shared_ptr<TextureResource> tr);

	// Destructor
	virtual ~Texture();

	// Inherited via Loadable
	virtual void load() override;

	// Inherited via Loadable
	virtual void freeResources() override;

	// Getters and setters
	GLuint getTextureId() const;
	void setTextureId(GLuint id);

	std::shared_ptr<TextureResource> getTextureResources();
	void setTextureResource(std::shared_ptr<TextureResource> textureResource);

private:
	void fetchData(GLenum target, GLchar* const sbuff, GLint sz, const GLchar* const buff, const GLint bytes, const GLint hf, const GLint W, const GLint wf);
};